# Mappa emoji -> parole intere
mapping = {
    "ğŸŸğŸŒ¡ğŸŒ‰ğŸŒ”ğŸŒ—": "flags",
    "ğŸŒ½ğŸ‡ğŸŒœ": "neocorp",
    "ğŸ«ğŸ‹ğŸŒµ": "manifesto",
    "ğŸ†": "civic",
    "ğŸŒ": "_"  # separatore tra parole
}

import re

def preprocess(seq):
    """Rimuove spazi e newline, collassa separatori multipli"""
    s = seq.strip()
    s = re.sub(r'ğŸŒ{2,}', 'ğŸŒ', s)
    return s

def decode(seq, mapping):
    """Decodifica sequenza emoji in parole intere usando la mappa"""
    decoded = seq
    # Ordina le chiavi per lunghezza decrescente per match piÃ¹ lungo prima
    for key in sorted(mapping.keys(), key=len, reverse=True):
        decoded = decoded.replace(key, mapping[key])
    return decoded

def normalize(decoded):
    """Collassa underscore multipli e rimuove ai bordi"""
    clean = re.sub(r'_+', '_', decoded).strip('_')
    return clean

def main():
    seq = preprocess(sequence)
    decoded = decode(seq, mapping)
    clean = normalize(decoded)
    
    flag = f"SMDCC{{{clean}}}"
    print("== Flag corretta ==")
    print(flag)

if __name__ == "__main__":
    main()
